47\\u0435\\u043b\\u043e\\u0432\\u0435\\u043a\\u0430, \\u043c\\u043d\\u0435 \\u044d\\u0442\\u043e \\u0431\\u044b\\u043b\\u043e \\u043e\\u0447\\u0435\\u043d\\u044c \\u0438\\u043d\\u0442\\u0435\\u0440\\u0435\\u0441\\u043d\\u043e. \\u041a\\u043e\\u0433\\u0434\\u0430 \\u043c\\u043d\\u0435 \\u0431\\u044b\\u043b\\u043e 15 \\u043b\\u0435\\u0442 \\u0434\\u0435\\u0434\\u0443\\u0448\\u043a\\u0438 \\u043d\\u0435 \\u0441\\u0442\\u0430\\u043b\\u043e, \\u0438 \\u044f \\u0447\\u0451\\u0442\\u043a\\u043e \\u043f\\u043e\\u043d\\u044f\\u043b\\u0430, \\u0447\\u0442\\u043e \\u044f \\u043f\\u043e\\u0439\\u0434\\u0443 \\u0442\\u043e\\u043b\\u044c\\u043a\\u043e \\u0432 \\u043c\\u0435\\u0434\\u0438\\u0446\\u0438\\u043d\\u0441\\u043a\\u0438\\u0439 \\u0443\\u043d\\u0438\\u0432\\u0435\\u0440\\u0441\\u0438\\u0442\\u0435\\u0442. \\u041d\\u0430 \\u043f\\u0435\\u0440\\u0432\\u043e\\u043c \\u043a\\u0443\\u0440\\u0441\\u0435 \\u044f \\u0443\\u0436\\u0435 \\u0437\\u043d\\u0430\\u043b\\u0430, \\u0447\\u0442\\u043e \\u0431\\u0443\\u0434\\u0443 \\u0432\\u0440\\u0430\\u0447\\u043e\\u043c \\u0430\\u043a\\u0443\\u0448\\u0435\\u0440-\\u0433\\u0438\\u043d\\u0435\\u043a\\u043e\\u043b\\u043e\\u0433\\u043e\\u043c. \\u041c\\u043d\\u0435 \\u043d\\u0440\\u0430\\u0432\\u0438\\u043b\\u043e\\u0441\\u044c \\u0437\\u0430\\u043d\\u0438\\u043c\\u0430\\u0442\\u044c\\u0441\\u044f \\u0433\\u0438\\u043d\\u0435\\u043a\\u043e\\u043b\\u043e\\u0433\\u0438\\u0447\\u0435\\u0441\\u043a\\u0438\\u043c\\u0438 \\u0437\\u0430\\u0431\\u043e\\u043b\\u0435\\u0432\\u0430\\u043d\\u0438\\u044f\\u043c\\u0438, \\u0443\\u0437\\u043d\\u0430\\u0432\\u0430\\u0442\\u044c, \\u043f\\u043e\\u0447\\u0435\\u043c\\u0443 \\u0442\\u0430\\u043a\\u0438\\u0435 \\u043f\\u0440\\u043e\\u0431\\u043b\\u0435\\u043c\\u044b \\u0443 \\u0436\\u0435\\u043d\\u0449\\u0438\\u043d\\u044b, \\u043a\\u0430\\u043a \\u043f\\u043e\\u043c\\u043e\\u0447\\u044c \\u043f\\u0430\\u0446\\u0438\\u0435\\u043d\\u0442\\u043a\\u0435, \\u0447\\u0442\\u043e \\u0441\\u0434\\u0435\\u043b\\u0430\\