array('id' => '114','iid' => '10000779','id_resource' => '11539','uri' => 'vrachi/makurin-sergej-aleksandrovich','surname' => 'Макурин','name' => 'Сергей','middlename' => 'Александрович','fullname' => 'Макурин Сергей Александрович','photo' => 'makurin-sergej-aleksandrovich-.jpg','photo_type' => 'jpg','photos' => NULL,'videos' => NULL,'holiday' => '0','rating' => '100','rating5' => '0','comments' => '2','show_comments' => '1','child' => '0','pregnant' => '1','diseases' => NULL,'experience' => '2006','way_experience' => '<h2 class="media-heading">Профессиональная подготовка:</h2>
